import React from 'react'
import {Box, TextField, Button, styled} from '@mui/material' //Box is basically a div substitute in Material UI. it has sx prop which is inline styling without css files.    Box = div + styling + theme + convenience

//styled in MUI is just “making your own component(customBox) with CSS attached to it. Instead of writing styles again and again, you lock them once and reuse
//every time I call CustonBox styled component it will have these CSS... styled component name must start with CAPITAL. CSS needs semicolons
const CustomBox = styled(Box)`
   width: 380px;
  margin: 80px auto;
  padding: 32px 28px;
  display: flex;
  flex-direction: column;
  gap: 20px;
  background-color: #ffffff;
  border-radius: 12px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
  text-align: center;;
`
// when passing an html tah lke "img" we should pass it as string or we will get error.React / MUI components → pass directly
const Image = styled('img')

const Login = () => {

    const imageURL = 'https://www.sesta.it/wp-content/uploads/2021/03/logo-blog-sesta-trasparente.png'

  return ( 
    <CustomBox>
        <img src={imageURL} alt='login'/>
        <TextField variant="standard"/>
        <TextField variant="standard"/>
        <Button variant="contained">Login</Button>
        <Button variant="text">Create an account</Button>
    </CustomBox>
  )
}

export default Login
